mapper.select:
SELECT * FROM(
		SELECT rownum r_num, a.* FROM(
			SELECT * FROM news_t
			WHERE status = 0
			ORDER BY n_idx DESC
		) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
update news_t
set subject = #{subject},
	writer = #{writer},
	ip = #{ip},
	content = #{content}
	
	,file_name = #{f_name}
	,ori_name = #{o_name}
	
	where n_idx = #{n_idx} and pwd =#{pwd}

update news_t
set hit = hit+1
where n_idx = #{n_idx}
