mapper.select:
SELECT * FROM(
		SELECT rownum r_num, a.* FROM(
			SELECT * FROM wbs_t
			WHERE status = 0 and b_name = '자유'
			ORDER BY b_idx DESC
		) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

SELECT * FROM(
		SELECT rownum r_num, a.* FROM(
			SELECT * FROM wbs_t
			WHERE status = 0 and b_name = '공지'
			ORDER BY b_idx DESC
		) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

SELECT * FROM(
		SELECT rownum r_num, a.* FROM(
			SELECT * FROM wbs_t
			WHERE status = 0 and b_name = '뉴스'
			ORDER BY b_idx DESC
		) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
update wbs_t
set subject = #{subject},
	writer = #{writer},
	ip = #{ip},
	content = #{content}

	,file_name = #{f_name}
	,ori_name = #{o_name}

	where b_idx = #{b_idx} and pwd =#{pwd}

update wbs_t
set hit = hit+1
where b_idx = #{b_idx}
